function F = FunctionalAssembler(f,g,N)  % for the border  h2 = N^2;    % Grid to evaluate functions on  x = linspace(0,1,N+1);  [X,Y] = meshgrid(x,x);    % F on the grid  F = f(X(2:end-1,2:end-1),Y(2:end-1,2:end-1));    % g on the Border  F(:,1) = F(:,1) + h2 * g(X(2:end-1,1),Y(2:end-1,1));  F(:,end) = F(:,end) + h2 * g(X(2:end-1,end),Y(2:end-1,end));    F(1,:) = F(1,:) + h2 * g(X(1,2:end-1),Y(1,2:end-1));  F(end,:) = F(end,:)+ h2 * g(X(end,2:end-1),Y(end,2:end-1));    % Retuns F as a vector  F = reshape(F,(N-1)*(N-1),1);end