function [A,b] = assemble_dirichlet(grid, A, b, g)  % Id_g is the identity for the bondray  Id_g = diag(grid.nodes(3,:));  % A will be modifed in such a way that the rows only remain where lambda is   % not at the bondray. these rows are replaces withe the coresponing unit vector.  A = (speye(length(grid.nodes(3,:)))-Id_g)*A + Id_g;    % sets the nodel values to g at the left hand side  b(grid.nodes(3,:)==1) = g(grid.nodes(1:2,:))(grid.nodes(3,:)==1);end