warning('off', 'Octave:possible-matlab-short-circuit-operator');% Deines function to be unsesed laterlocal_assem_stiff = @(S,B,Q) assemble_stiff(S,B,Q);% f and g for the possion problemf = @(x) F_wtf(x);g = @(x) zeros(1,length(x(1,:)));%Defines baises and quatore rules from given filesB = basis(1);Q3 = quadrature(3);Q1 = quadrature(1);% Uniform girdM = 40grid = uniform_grid(M,M);% assemble A and b navilyA = assemble_P1(grid,local_assem_stiff,Q1);b = assemble_b(grid,f,Q3);% adjustet to baundary[A,b] = assemble_dirichlet(grid,A,b,g);%Compute solution U = A\b;% Preperation for plotx = grid.nodes(1,:);y = grid.nodes(2,:);T = grid.elements(1:3,:);%Plottrisurf(T',x,y,U)% same with circlegrid = load("circle.mat");A = assemble_P1(grid,local_assem_stiff,Q1);b = assemble_b(grid,f,Q3);[A,b] = assemble_dirichlet(grid,A,b,g);U = A\b;x = grid.nodes(1,:);y = grid.nodes(2,:);T = grid.elements(1:3,:);trisurf(T',x,y,U)